import React, { useEffect, useState } from 'react';
import './Header.css';

const Header = () => {
    const [scrolled, setScrolled] = useState(false);

    const handleScroll = () => {
        const offset = window.scrollY;
        if (offset > 50) {
            setScrolled(true);
        } else {
            setScrolled(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);


    return (
        <header className={scrolled ? 'header scrolled' : 'header'}>
            <nav className='header-navigation'>
                <ul className='header-navigation-list'>
                    <li className='header-navigation-item'><a href='/services'>SERVICES</a></li>
                    <li className='header-navigation-item'><a href='/work'>OUR WORK</a></li>
                    <li className='header-navigation-item'><a href='/about'>ABOUT US</a></li>
                    <li className='header-navigation-item'><a href='/blog'>BLOG</a></li>
                </ul>
            </nav>
            <div className='header-logo'>
                <img src="//www.mindarc.com.au/cdn/shop/t/5/assets/mindarc-logo-blk.png?v=152472296200138488861589258936" alt="MindArc" />
            </div>
            <div className='header-contact'>
                <a href='/contact' className='header-contact-link btn btn-primary'>CONTACT US!</a>
                <span className='header-contact-number'>1300 206 827</span>
            </div>
        </header>
    );
};

export default Header;
