import React from "react";
import amazonLocker from '../../pics/amazonLocker.jpg';
import youtube from '../../pics/youtube.jpg';
import fiveg from '../../pics/fiveg.jpg';


const images = {
    amazonLocker,
    youtube,
    fiveg,
};


const NewsArticle = ({ title, content, image, readMoreLink }) => {

    return (
        <div className="news-article">
            <img src={images[image]} alt={title} className="news-image" />
            <div className="news-content">
                <h2>{title}</h2>
                <p>{content}</p>
                <a href={readMoreLink}>Read More</a>
            </div>
        </div>
    );
};

export default NewsArticle;
