import React, { useEffect, useState } from 'react';
import './Intro.css';
// Import logos if you have them saved locally
// import logoShopify from './path-to-shopify-logo.png';


const sentences = [
    "Shopify Integration Specialists.",
    "Digital Enthusiasts.",
    "Shopify Plus Partners.",
    "SEO, SEM, CRO, Social & content Specialists.",
    "Telecommunications Experts."
];


const Intro = () => {
    const [index, setIndex] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setIndex((prevIndex) => (prevIndex + 1) % sentences.length);
        }, 3000);

        return () => clearInterval(timer);
    }, []);



  return (
    <div className="intro">
        <h1>MindArc</h1>
        <h2>Who is MindArc? <span className="intro-highlight">{sentences[index]}</span></h2>
        <p>Since 2010, our passionate team of technology experts have followed a path of relentless learning and experimentation in all things digital, retail, and everything in-between. Our vision is to apply our collective experience and help enable and transform commerce through smart technology...</p>
        <div className="intro-logos">
            {/* Use <img> tags if you have local logo images */}
            {/* <img src={logoShopify} alt="Shopify Plus" />
            <img src={...} alt="..." />
            ... */}
            {/* If you're using external links or have the logos saved in the public folder, you can directly reference them like this: */}
            <img src="/path-to-shopify-logo.png" alt="Shopify Plus" />
            {/* Repeat for other logos */}
        </div>
    </div>
  );
};

export default Intro;
