import React from 'react';
import './Approach.css'; 

const Approach = () => {
  return (
    <div className="Approach">
      <div className="wave-container">
        {/* Your wave animation will go here */}
      </div>
      <div className="approach-content">
        <h1 className='approach-heading'>Our Holistic Approach</h1>
        <div className='services'>
            <div className="service">
            <h2>MindArc Digital</h2>
            <p>Strategy, design, implementation, and ongoing eCommerce website maintenance...</p>
            <button className="service-button">Explore MindArc Digital</button>
            </div>
            <div className="service">
            <h2>MindArc Marketing</h2>
            <p>Our team of SEO and content experts...</p>
            <button className="service-button">Explore MindArc Marketing</button>
            </div>
            <div className="service">
            <h2>MindArc Telecommunications</h2>
            <p>We have been working with leading telecommunications providers...</p>
            <button className="service-button">Explore MindArc Telco</button>
            </div>
        </div>
      </div>
    </div>
  );
};

export default Approach;
